mapper.select:
select
  id, type, name, url, parent_id, parent_ids, permission, available
from metropolis.sys_resources


        and id = #{id}


        and type = #{type}


        and name = #{name}


        and url = #{url}


        and parent_id = #{parentId}


        and parent_ids = #{parentIds}


        and permission = #{permission}


        and available = #{available}
